//Декларація для модульних стилів
declare module '*.module.css';
// Декларація для типу JPG
declare module '*.jpg' {
  const src: string;
  export default src;
}

// Декларація для типу PNG
declare module '*.png' {
  const src: string;
  export default src;
}

// Декларація для типу SVG
declare module '*.svg' {
  const src: string;
  export default src;
}
